script:
frappe.ui.form.on('Job Details', {
    refresh: function(frm) {
        if (frm.doc.__islocal) {
            // New unsaved document, don't remove the "Not Saved" badge
        } else {
            frm.page.clear_indicator('Not Saved');
        }
    }
});

frappe.ui.form.on('Sales Invoice Item', {
    item_code: function(frm, cdt, cdn) {
        let row = locals[cdt][cdn];
        console.log(row);
        console.log(cdt);
        console.log(cdn);

        let item_code = row.item_code;
        console.log(item_code);

        frappe.call({
            method: 'frappe.client.get_value',
            args: {
                'doctype': 'Item',
                'filters': { 'item_code': item_code },
                'fieldname': 'item_name'
            },
            callback: function (r) {
                if (!r.exc) {
                    console.log(r.message);
                    // Update the 'item_name' field in the Sales Invoice Item form
                    frappe.model.set_value(cdt, cdn, 'item_name', r.message.item_name);
                    // Set the 'conversion_factor' field to 1
                    frappe.model.set_value(cdt, cdn, 'conversion_factor', 1);

                    // Update 'base_rate' and 'base_amount' to match 'rate' and 'amount'
                    frappe.model.set_value(cdt, cdn, 'base_rate', row.rate);
                    frappe.model.set_value(cdt, cdn, 'base_amount', row.amount);

                    // Set 'price_list_rate' and 'price_list_rate_with_tax' to match 'rate'
                    frappe.model.set_value(cdt, cdn, 'price_list_rate', row.rate);
                    frappe.model.set_value(cdt, cdn, 'price_list_rate_with_tax', row.rate);
                }
            }
        });
    },
    qty: function(frm, cdt, cdn) {
        calculateAmountAndRate(cdt, cdn);
    },
    rate: function(frm, cdt, cdn) {
        calculateAmountAndRate(cdt, cdn);

        // Update 'base_rate' to match 'rate' when 'rate' is changed
        let row = locals[cdt][cdn];
        frappe.model.set_value(cdt, cdn, 'base_rate', row.rate);

        // Update 'price_list_rate' and 'price_list_rate_with_tax' to match 'rate'
        frappe.model.set_value(cdt, cdn, 'price_list_rate', row.rate);
        frappe.model.set_value(cdt, cdn, 'base_price_list_rate', row.rate);
    }
});

function calculateAmountAndRate(cdt, cdn) {
    let row = locals[cdt][cdn];
    let qty = row.qty;
    let rate = row.rate;

    // Calculate the 'Amount'
    let amount = qty * rate;
    frappe.model.set_value(cdt, cdn, 'amount', amount);

    // Set 'Rate (Company Currency)' to the same value as 'Rate'
    frappe.model.set_value(cdt, cdn, 'base_rate', rate);

    // Update 'base_amount' to match 'amount'
    frappe.model.set_value(cdt, cdn, 'base_amount', amount);
}

frappe.ui.form.on('Sales Invoice', {
  refresh: function(frm) {
    if (frm.doc.__islocal) {
      frm.add_custom_button(__('Sales Order'), function() {
        erpnext.utils.map_current_doc({
          method: "erpnext.selling.doctype.sales_order.sales_order.make_sales_invoice",
          source_doctype: "Sales Order",
          target: frm,
          setters: {
            customer: frm.doc.customer ||"",
            custom_daily_log: frm.doc.custom_daily_log || "",
          },
          get_query_filters: {
            docstatus: 1,
            status: ["not in", ["Closed", "On Hold"]],
            per_billed: ["<", 99.99],
            company: frm.doc.company,
          }
        });
      }, __("Get Items From"));
    }
  }
});

frappe.ui.form.on('Sales Invoice', {
    refresh: function(frm) {
        const selectedLogs = [];
        frm.add_custom_button(__('Select Daily Log'), function() {
            const dialog = new frappe.ui.form.MultiSelectDialog({
                doctype: "Daily Log",
                target: frm,
                setters: {},
                add_filters_group: 1,
                date_field: "transaction_date",
                columns: ["vehicle", "driver"],
                get_query() {
                    return {
                        filters: { docstatus: ['!=', 2] }
                    };
                },
                primary_action_label: 'Get Details',
                action(selections, args) {
                    selections.forEach(selectedItem => {
                        const logName = selectedItem;
                        if (!selectedLogs.includes(logName)) {
                            frappe.call({
                                method: 'frappe.client.get',
                                args: {
                                    doctype: 'Daily Log',
                                    name: logName,
                                },
                                callback: function(response) {
                                    if (response.message) {
                                        const dailyLogData = response.message;
                                        dailyLogData.items.forEach(dailyLogItem => {
                                            const newRow = frm.add_child('items');
                                            newRow.item_code = dailyLogItem.item_code;
                                            newRow.qty = dailyLogItem.qty;
                                        });
                                        frm.refresh_field('items');
                                        selectedLogs.push(logName);
                                        frappe.show_alert(__('<span style="color: green;">Daily Log "{0}" items added successfully.</span>', [logName]), 6);
                                    } else {
                                        console.error('Error fetching Daily Log data.');
                                        frappe.show_alert(__('<span style="color: red;">Error fetching Daily Log data for "{0}".</span>', [logName]), 6);
                                    }
                                }
                            });
                        } else {
                            frappe.show_alert(__('<span style="color: red;">Daily Log "{0}" items already exist in the Sales Invoice.</span>', [logName]), 6);
                        }
                    });
                }
            });
        });
    }
});
