css:
.heading{
    background-color:rgba(31,55,93,255);
    padding:8px 0px;
    text-align:center;
    color:white;
    font-weight:700;
}
.blue{
    background-color:rgba(31,55,93,255);
    color:white;
}
.flex-container {
    display: flex;
    justify-content: space-between;
    width: 100%;
}
.left-div, .right-div {
    flex: 1;
    display: flex;
}
.col{
    display:inline-block;
}
.row{
    display:flex;
}
table, th, td {
  border: 2px solid black;
}
table{
    margin-top:20px;
}
.border{
    border:2px solid black;
}
.text-center{
    justify-content:center;
}
p{
    font-size:10px;
}
